    @EventDetailOdd DECIMAL(18, 7), 
    @FinishingPosition SMALLINT, 
    @FirstTimer BIT
AS

BEGIN
	INSERT INTO dbo.Tournament(TournamentName)
	VALUES(@TournamentName);

	SET @Id = (SELECT SCOPE_IDENTITY());
END

